var sushis=0,riceprice=10,octopusprice=20,sashimiprice=40,sushiprice=80,souschefprice=120;const ricevalue=1,octopusvalue=2,sashimivalue=3,sushivalue=4,souschefvalue=5;var interval;function sushiClicker(a){sushis=sushis+a;document.getElementById("sushis").innerHTML=sushis;unlockUpgrades(sushis)}function buy(a){var b=getPriceAndValue(a);sushis=sushis-b[0];document.getElementById("sushis").innerHTML=sushis;var c=b[1]*1+b[3];console.log(a.id+" "+b[1]);console.log("Calculated value "+c);updatePrice(a);unlockUpgrades(sushis);clearInterval(interval);interval=window.setInterval(()=>{sushiClicker(1)},1000/c)}function unlockUpgrades(a){if(a<riceprice){document.getElementById("rice").setAttribute("disabled","true")}else{document.getElementById("rice").removeAttribute("disabled")}if(a<octopusprice){document.getElementById("octopus").setAttribute("disabled","true")}else{document.getElementById("octopus").removeAttribute("disabled")}if(a<sashimiprice){document.getElementById("sashimi").setAttribute("disabled","true")}else{document.getElementById("sashimi").removeAttribute("disabled")}if(a<sushiprice){document.getElementById("sushi").setAttribute("disabled","true")}else{document.getElementById("sushi").removeAttribute("disabled")}if(a<souschefprice){document.getElementById("sousChef").setAttribute("disabled","true")}else{document.getElementById("sousChef").removeAttribute("disabled")}}function getPriceAndValue(a){var b=[];if(a.id=="rice"){b[0]=riceprice;b[1]=ricevalue;b[2]=document.getElementById("ricebought").innerHTML}else if(a.id=="octopus"){b[0]=octopusprice;b[1]=octopusvalue;b[2]=document.getElementById("octopusinfo").innerHTML}else if(a.id=="sashimi"){b[0]=sashimiprice;b[1]=sashimivalue;b[2]=document.getElementById("sashimiinfo").innerHTML}else if(a.id=="sushi"){b[0]=sushiprice;b[1]=sushivalue;b[2]=document.getElementById("sushiinfo").innerHTML}else if(a.id=="sousChef"){b[0]=souschefprice;b[1]=souschefvalue;b[2]=document.getElementById("souschefinfo").innerHTML}b[3]=previouslyBought();return b}function previouslyBought(){var a;a=parseInt(document.getElementById("ricebought").innerHTML)*ricevalue;a=a+parseInt(document.getElementById("octopusinfo").innerHTML)*octopusvalue;a=a+parseInt(document.getElementById("sashimiinfo").innerHTML)*sashimivalue;a=a+parseInt(document.getElementById("sushiinfo").innerHTML)*sushivalue;a=a+parseInt(document.getElementById("souschefinfo").innerHTML)*souschefvalue;return a}function updatePrice(a){if(a.id=="rice"){riceprice=riceprice+1;document.getElementById("ricebought").innerHTML=parseInt(document.getElementById("ricebought").innerHTML)+1;document.getElementById(a.id).innerHTML="RICE "+riceprice+" CLICKS"}else if(a.id=="octopus"){octopusprice=octopusprice+1;document.getElementById("octopusinfo").innerHTML=parseInt(document.getElementById("octopusinfo").innerHTML)+1;document.getElementById(a.id).innerHTML="OCTOPUS "+octopusprice+" CLICKS"}else if(a.id=="sashimi"){sashimiprice=sashimiprice+1;document.getElementById("sashimiinfo").innerHTML=parseInt(document.getElementById("sashimiinfo").innerHTML)+1;document.getElementById(a.id).innerHTML="SASHIMI "+sashimiprice+" CLICKS"}else if(a.id=="sushi"){sushiprice=sushiprice+1;document.getElementById("sushiinfo").innerHTML=parseInt(document.getElementById("sushiinfo").innerHTML)+1;document.getElementById(a.id).innerHTML="SUSHI "+sushiprice+" CLICKS"}else if(a.id=="sousChef"){souschefprice=souschefprice+1;document.getElementById("souschefinfo").innerHTML=parseInt(document.getElementById("souschefinfo").innerHTML)+1;document.getElementById(a.id).innerHTML="SOUS CHEF "+souschefprice+" CLICKS"}}